mapper.select:
    SELECT

    FROM tb_item i
    LEFT JOIN tb_item_cat c ON i.cid = c.id
    where i.status != 3



         /* 中文排序解决
          ORDER BY  CONVERT(i.${order.sort} USING gbk)  ${order.order}*/

--            order by i.title asc ,i.price asc
           i.${item}



        ORDER BY i.id DESC


    LIMIT #{page.offset},#{page.rows}
